import { Component, Input, inject } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatMenuModule } from '@angular/material/menu';
import { MatIconModule } from '@angular/material/icon';
import { MatDividerModule } from '@angular/material/divider';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

import { User } from 'worm-api-client';
import { UserSettingsService } from '@services/user-settings.service';

interface LogoutResponse {
  message: string;
  redirectUrl: string;
}

@Component({
  selector: 'app-user-menu',
  standalone: true,
  imports: [MatButtonModule, MatMenuModule, MatIconModule, MatDividerModule],
  templateUrl: './user-menu.component.html',
  styleUrl: './user-menu.component.scss',
})
export class UserMenuComponent {
  @Input() user: User | undefined = undefined;

  private userSettings = inject(UserSettingsService);
  private router = inject(Router);
  private http = inject(HttpClient);

  onLogout() {
    this.http
      .post<LogoutResponse>('/logout', {}, { withCredentials: true })
      .subscribe({
        next: response => {
          if (response && response.redirectUrl) {
            this.router.navigateByUrl(response.redirectUrl);
          } else {
            this.router.navigateByUrl('/welcome');
          }
        },
        error: error => {
          console.error('Logout failed', error);
        },
      });
  }

  onSettings() {
    this.userSettings.openSettingsDialog();
  }

  onFiles() {
    this.userSettings.openFileDialog();
  }
}

import { Component, Input, inject, OnInit, OnDestroy } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import {
  MatDialogContent,
  MatDialogTitle,
  MatDialogActions,
  MatDialogClose,
} from '@angular/material/dialog';
import { MatIconModule } from '@angular/material/icon';
import { MatListModule } from '@angular/material/list';
import { MatTooltipModule } from '@angular/material/tooltip';
import { GeneralSettingsComponent } from './tabs/general-settings/general-settings.component';
import {
  trigger,
  transition,
  style,
  animate,
  query,
  group,
} from '@angular/animations';
import { BreakpointObserver, Breakpoints } from '@angular/cdk/layout';
import { Subject, takeUntil } from 'rxjs';
import { CommonModule } from '@angular/common';

const slideAnimation = trigger('slideAnimation', [
  transition(
    '* => *',
    [
      query(':enter, :leave', style({ position: 'absolute', width: '100%' }), {
        optional: true,
      }),
      group([
        query(
          ':enter',
          [
            style({ transform: 'translateY({{enterTransform}})' }),
            animate('300ms ease-out', style({ transform: 'translateY(0%)' })),
          ],
          { optional: true }
        ),
        query(
          ':leave',
          [
            animate(
              '300ms ease-out',
              style({ transform: 'translateY({{leaveTransform}})' })
            ),
          ],
          { optional: true }
        ),
      ]),
    ],
    { params: { enterTransform: '100%', leaveTransform: '-100%' } }
  ),
]);

@Component({
  selector: 'app-user-settings-dialog',
  standalone: true,
  imports: [
    CommonModule,
    MatDialogTitle,
    MatDialogContent,
    MatDialogActions,
    MatDialogClose,
    MatListModule,
    MatButtonModule,
    MatIconModule,
    MatTooltipModule,
    GeneralSettingsComponent,
  ],
  animations: [slideAnimation],
  templateUrl: './user-settings-dialog.component.html',
  styleUrl: './user-settings-dialog.component.scss',
})
export class UserSettingsDialogComponent implements OnInit, OnDestroy {
  @Input() selectedCategory: 'general' | 'account' = 'general';
  previousCategory: 'general' | 'account' = 'general';
  isMobile = false;
  private destroyed = new Subject<void>();
  private breakpointObserver = inject(BreakpointObserver);

  ngOnInit() {
    this.breakpointObserver
      .observe([Breakpoints.HandsetPortrait, Breakpoints.TabletPortrait])
      .pipe(takeUntil(this.destroyed))
      .subscribe(result => {
        this.isMobile = result.matches;
      });
  }

  ngOnDestroy() {
    this.destroyed.next();
    this.destroyed.complete();
  }

  selectCategory(category: 'general' | 'account') {
    this.previousCategory = this.selectedCategory;
    this.selectedCategory = category;
  }

  getAnimationState() {
    const isMovingDown =
      this.selectedCategory === 'account' &&
      this.previousCategory === 'general';
    return {
      value: this.selectedCategory,
      params: {
        enterTransform: isMovingDown ? '100%' : '-100%',
        leaveTransform: isMovingDown ? '-100%' : '100%',
      },
    };
  }
}

import { Component, NgZone, OnDestroy, OnInit, inject } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { RouterLink, Router } from '@angular/router';
import { MatCardModule } from '@angular/material/card';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatDividerModule } from '@angular/material/divider';
import { CommonModule } from '@angular/common';
import { BreakpointObserver, Breakpoints } from '@angular/cdk/layout';
import { firstValueFrom, Subject, takeUntil } from 'rxjs';
import { UserAPIService } from 'worm-api-client';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { MatSnackBar } from '@angular/material/snack-bar';
import { XsrfService } from '@services/xsrf.service';

@Component({
  selector: 'app-welcome',
  standalone: true,
  imports: [
    CommonModule,
    FormsModule,
    RouterLink,
    MatCardModule,
    MatFormFieldModule,
    MatInputModule,
    MatButtonModule,
    MatIconModule,
    MatDividerModule,
  ],
  templateUrl: './welcome.component.html',
  styleUrl: './welcome.component.scss',
})
export class WelcomeComponent implements OnInit, OnDestroy {
  private breakpointObserver = inject(BreakpointObserver);
  private ngZone = inject(NgZone);
  private userService = inject(UserAPIService);
  private http = inject(HttpClient);
  private router = inject(Router);
  private snackBar = inject(MatSnackBar);
  private xsrfService = inject(XsrfService);
  isMobile = false;
  private destroy$ = new Subject<void>();

  username = '';
  password = '';
  githubEnabled = false;
  googleEnabled = false;
  facebookEnabled = false;
  discordEnabled = false;
  appleEnabled = false;

  constructor() {
    this.setupBreakpointObserver();
  }

  async ngOnInit(): Promise<void> {
    const providers = await firstValueFrom(
      this.userService.getEnabledOAuth2Providers()
    );
    console.log('Enabled OAuth2 providers:', providers);
    providers.forEach(provider => {
      if (provider === 'github') {
        this.githubEnabled = true;
      }
      if (provider === 'google') {
        this.googleEnabled = true;
      }
      if (provider === 'facebook') {
        this.facebookEnabled = true;
      }
      if (provider === 'discord') {
        this.discordEnabled = true;
      }
      if (provider === 'apple') {
        this.appleEnabled = true;
      }
    });
  }

  setupBreakpointObserver() {
    this.breakpointObserver
      .observe([Breakpoints.XSmall, Breakpoints.Small])
      .pipe(takeUntil(this.destroy$))
      .subscribe(result => {
        this.isMobile = result.matches;
      });
  }

  onLogin() {
    const body = new URLSearchParams();
    body.set('username', this.username);
    body.set('password', this.password);

    const xsrfToken = this.xsrfService.getXsrfToken();
    const headers = new HttpHeaders({
      'Content-Type': 'application/x-www-form-urlencoded',
      'X-XSRF-TOKEN': xsrfToken,
    });

    this.http
      .post('/login', body.toString(), {
        headers,
        observe: 'response',
        withCredentials: true,
      })
      .subscribe({
        next: response => {
          console.log('Login successful', response);
          this.snackBar.open('Login successful', 'Close', { duration: 3000 });
          this.router.navigate(['/']);
        },
        error: error => {
          console.error('Login failed', error);
          this.snackBar.open(
            'Login failed. Please check your credentials.',
            'Close',
            { duration: 5000 }
          );
        },
      });
  }

  loginWithGoogle() {
    console.log('Google login attempted');
    this.ngZone.runOutsideAngular(() => {
      window.location.href = '/oauth2/authorization/google';
    });
  }

  loginWithFacebook() {
    console.log('Facebook login attempted');
    this.ngZone.runOutsideAngular(() => {
      window.location.href = '/oauth2/authorization/facebook';
    });
  }

  loginWithGithub() {
    console.log('GitHub login attempted');
    this.ngZone.runOutsideAngular(() => {
      window.location.href = '/oauth2/authorization/github';
    });
  }

  loginWithApple() {
    console.log('Apple login attempted');
    this.ngZone.runOutsideAngular(() => {
      window.location.href = '/oauth2/authorization/apple';
    });
  }

  loginWithDiscord() {
    console.log('Discord login attempted');
    this.ngZone.runOutsideAngular(() => {
      window.location.href = '/oauth2/authorization/discord';
    });
  }

  ngOnDestroy() {
    this.destroy$.next();
    this.destroy$.complete();
  }
}

